"use strict";var hourHand=document.getElementById("hour-hand"),minuteHand=document.getElementById("minute-hand"),secondHand=document.getElementById("second-hand");function setClock(){var e=new Date,t=e.getHours()%12*30+30*e.getMinutes()/60,n=6*e.getMinutes()+6*e.getSeconds()/60,o=6*e.getSeconds();secondHand.style.transform="rotate(".concat(o,"deg)"),minuteHand.style.transform="rotate(".concat(n,"deg)"),hourHand.style.transform="rotate(".concat(t,"deg)")}document.addEventListener("DOMContentLoaded",(function(){setClock(),document.getElementById("clock").style.opacity="1",setInterval((function(){return setClock()}),1e3)}));